onCombatStart.script:
const proceed = await foundry.applications.api.DialogV2.confirm({
  content: `<img src="worlds/etrian-odyssey/data/assets/feats-icons/000823_hr1.png" class="center">Un combat commence ! Souhaitez-vous activer la Poussée sanguine en tant qu'action libre ?`,
  rejectClose: false,
  modal: true,
  window: {title: "Poussée sanguine" }
});
if ( proceed ) {
  dnd5e.documents.macro.rollItem("Poussée sanguine (Spécial)")
  ui.notifications.info(`Poussée sanguine activée`);
}

onTurnStart.script:
let damageRoll = await new Roll(`@scale.sentinelle.bloodsurgehp.value`, actor.getRollData()).evaluate();
await damageRoll.toMessage({flavor: `Dégâts de Poussée sanguine`,
user: game.user._id,
speaker: ChatMessage.getSpeaker({actor: actor})
});
actor.applyDamage(damageRoll.result);

onCombatEnd.script:
const proceed = await foundry.applications.api.DialogV2.confirm({
  content: `<img src="worlds/etrian-odyssey/data/assets/feats-icons/000823_hr1.png" class="center">Le combat est terminé ! Souhaitez-vous utiliser Souffle en tant qu'action libre ?`,
  rejectClose: false,
  modal: true,
  window: {title: "Souffle" }
});
if ( proceed ) {
  let damageRoll = await new Roll(`-1d(@scale.sentinelle.breathhp.faces / 2)`, 
  actor.getRollData()).evaluate();
  await damageRoll.toMessage({flavor: `Soin de Souffle`,
  user: game.user._id,
  speaker: ChatMessage.getSpeaker({actor: actor})
  });
  actor.applyDamage(damageRoll.result);
}

macro.command:
if (args[0].macroPass == "postDamageRoll") {
    let equippedweapon = actor.items.filter(i => i.type == "weapon" && i.system.equipped)[0]
    if (item.name != equippedweapon.name) return;

    let overheat = actor.effects.find(e => e.name == "Surchauffe");
    if (overheat) {
    const roundEnd = overheat.duration.rounds - 1;
    await overheat.update({"duration.rounds" : roundEnd});
    if (overheat.duration.rounds <= 0) {
        overheat.delete();
    }
}
}
if (args[0]=="each") actor.reset();

const source = canvas.tokens.controlled[0];

if (!source) {
    ui.notifications.error("No token selected!");
    return;
}

let target = game.user.targets.size ? Array.from(game.user.targets)[0] : null; // Get the first targeted token
if (!target) {
    ui.notifications.error("No target selected!");
    return;
}

let sourcePosition = { x: source.document.x +100, y: source.document.y +100};
let targetPosition = { x: target.document.x +100, y: target.document.y +100};

async function swap(source, target){
new Sequence()
    .effect()
        .file("jb2a.chain_lightning.primary.blue")
        .atLocation(source)
        .stretchTo(target)
    .wait(750)
    .animation()
        .on(source)
        .teleportTo({ x: target.document.x +100, y: target.document.y +100})
        .snapToGrid()
        .waitUntilFinished()
    .wait(1500)
    .play()
}
await swap(source, target)
await swap(target, source)

if (args[0].macroPass == "postActiveEffects") {
    if (item.name == "Assaut cinétique - 3 PA (Action Complexe)" || item.name == "Feu cinétique - 3 PA (Action Complexe)" || item.name == "Gel cinétique - 3 PA (Action Complexe)" || item.name == "Foudre cinétique - 3 PA (Action Complexe)" || item.name == "Accélération cinétique - 3 PA (Action Complexe)") {

    let overheat = actor.effects.find(e => e.name == "Surchauffe");
    const roundEnd = overheat.duration.rounds - actor.system.scale.destruction.intercooler.value;
    await overheat.update({"duration.rounds" : roundEnd});
    if (overheat.duration.rounds <= 0) {
        overheat.delete();
    }
    } else {
        return;
    }
}

if (args[0]=="each") actor.reset();

const effectNames = ['À terre', 'Inconscient', 'Endormi'];
const targetActor = workflow.hitTargets.first().actor;

// Vérifie si l'effet "Inconscient" est présent
const isUnconscious = targetActor.appliedEffects.some(ef => ef.name === 'Inconscient');

effectNames.forEach(effectName => {
    // Si l'effet est "À terre" et que "Inconscient" est présent, ne pas le retirer
    if (effectName === 'À terre' && isUnconscious) return;

    const effect = targetActor.appliedEffects.find(ef => ef.name === effectName);
    if (effect) effect.delete();
});

onCombatStart.script:
let momentum = actor.effects.find(e => e.name == "Vélocité");
if (!momentum) {
momentum = item.effects.find(e => e.name == "Vélocité");
await actor.createEmbeddedDocuments('ActiveEffect', [momentum])
} else {
const roundDelta = Math.min(5 - momentum.duration.remaining, 3);
const roundEnd = momentum.duration.rounds + roundDelta;
await momentum.update({"duration.rounds" : roundEnd});
}
